$:/ak/drift/ui/transclude.css.text:
.tc-sidebar-lists button.tc-btn-mini { 
   color: rgb(179,179,179);
}
.tooltip {
  position: relative;
  display: inline-block;
}

.tooltip .tooltiptext {
  visibility: hidden;
  overflow: hidden;
  margin-top: -2em;
  padding: 5px;
  display: block;

  /* Position the tooltip */
  position: absolute;
  z-index: 80;

  /*  
  top: 20px;
  left: 20px;  */

  float: right;  
  opacity: 0;

   /* transition: opacity 0.3s; */
}

.tooltip .tooltiptext::after {
  position: relative;
  overflow: hidden;
  margin: 0;
  padding: 0;
  border: 1px;
}

.tooltip:hover > .tooltiptext {
   margin-top:-5px;
  visibility: visible;
  opacity: 1;
  width: 100vw;
  max-width: 32em;
  height: 100vw;
  max-height: 32em;
}

.resp-iframe {
    position: rel;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border: 1px;
}
