$:/ak/plugins/DailyNotes/stylesheet.text:
input.dn-input.input-large { width: 100%; padding: .5em 1em; font-size: 1.1em; }
.dn-label { display: block; text-align: right; font-size: .8em; opacity: .6; }
input.dn-input:focus { border-color: rgb(255, 201, 102); }

.DN-entries .DN-deleteButton { 
 opacity: 0; 
}

.DN-entries:hover .DN-deleteButton { 
 opacity: 1;   
 fill: grey;
}

.DN-entries:hover .DN-deleteButton:hover { 
fill: #c70000 ;  
}

.DN-entries .DN-editButton { 
opacity: 0; 
}


.DN-entries:hover .DN-editButton { 
opacity: 1;  
}

.DN-editButton:hover { 
fill: #228c22 ;  
}

.DN-check-edit input{display:none;}
.DN-check-edit svg{fill:grey;height:1em;width:1em;curser:pointer;}
.DN-check-edit:hover svg{ fill:green; }
